# grid = [[0,1,0,0],[1,1,1,0],[0,1,0,0],[1,1,0,0]]

perimeter = 0

grid = [[0, 1, 0, 0], [1, 1, 1, 0], [0, 1, 0, 0], [1, 1, 0, 0]]

perimeter = 0

for row in grid:
    print(row)

for i in range(len(grid)):
    for j in range(len(grid[i])):
        if grid[i][j] == 1:
            perimeter += 4

            # Check top
            if i != 0 and grid[i - 1][j] == 1:
                print("Row: ", i, "Col: ", j, "has a box on top")
                perimeter -= 1

            # Check left
            if j != 0 and grid[i][j - 1] == 1:
                print("Row: ", i, "Col: ", j, "has a box on left")
                perimeter -= 1

            # Check right
            if j != len(grid[i]) - 1 and grid[i][j + 1] == 1:
                print("Row: ", i, "Col: ", j, "has a box on right")
                perimeter -= 1

            # Check bottom
            if i != len(grid) - 1 and grid[i + 1][j] == 1:
                print("Row: ", i, "Col: ", j, "has a box on bottom")
                perimeter -= 1

print(perimeter)
